mapper.select:
SELECT product_id AS productId, product_name AS productName, category_id AS categoryId, tag, price, reg_dt AS registerDate, main_img AS mainImg FROM smartstore_db.product
category_id = #{categoryId}
AND (product_name LIKE CONCAT('%',#{searchKeyword},'%') OR tag LIKE CONCAT('%',#{searchKeyword},'%'))
ORDER BY
reg_dt DESC price price DESC
LIMIT #{pagingOffsetDto.offsetNum}, #{pagingOffsetDto.recordCountPerPage};

SELECT COUNT(*) FROM smartstore_db.product
category_id = #{categoryId}
AND (product_name LIKE CONCAT('%',#{searchKeyword},'%') OR tag LIKE CONCAT('%',#{searchKeyword},'%'))

mapper.delete:
DELETE
from smartstore_db.product
WHERE product_id = #{productId}
  AND store_id = #{storeId};
